00379 bool DIOSPITFTDISPLAYILI9341::TFT_RotateRGB(int rotate, XBYTE bgr)
00380 {
00381   #define MEM_Y   (7) /* MY row address order */
00382   #define MEM_X   (6) /* MX column address order */
00383   #define MEM_V   (5) /* MV row / column exchange */
00384   #define MEM_L   (4) /* ML vertical refresh order */
00385   #define MEM_H   (2) /* MH horizontal refresh order */
00386   #define MEM_BGR (3) /* RGB-BGR Order */
00387 
00388   bool status = false;
00389 
00390   switch(rotate)
00391     {
00392       case    0 : status = WriteRegister(0x36, 1, (1 << MEM_X) | (bgr << MEM_BGR));                               break;
00393       case   90 : status = WriteRegister(0x36, 1, (1 << MEM_Y) | (1 << MEM_X) |(1 << MEM_V) | (bgr << MEM_BGR));  break;
00394       case  180 : status = WriteRegister(0x36, 1, (1 << MEM_Y) | (bgr << MEM_BGR));                               break;
00395       case  270 : status = WriteRegister(0x36, 1, (1 << MEM_V) | (1 << MEM_L) | (bgr << MEM_BGR));                break;
00396       default   : break;
00397     }
00398 
00399   return status;
00400 }
