00417 bool DIOSPITFTDISPLAYILI9341::TFT_SetWindow(int xs, int ys, int xe, int ye)
00418 {
00419   bool status;
00420 
00421   /* Column address set */
00422   status = WriteRegister(0x2A, 4,  (xs >> 8) & 0xFF, xs & 0xFF, (xe >> 8) & 0xFF, xe & 0xFF);
00423 
00424   /* Row adress set */
00425   if(status) status = WriteRegister(0x2B, 4,  (ys >> 8) & 0xFF, ys & 0xFF, (ye >> 8) & 0xFF, ye & 0xFF);
00426 
00427   return status;
00428 }
