00143 
00144               GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET)     , true);    GEN_XSLEEP.MilliSeconds(50);
00145               GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET)     , false);   GEN_XSLEEP.MilliSeconds(50);
00146               GEN_DIOGPIO.SetValue(GetGPIOEntryID(DIODISPLAYDEVICE_INDEX_GPIOENTRYID_RESET)     , true);    GEN_XSLEEP.MilliSeconds(50);    
00147             }              
