00173 bool DIOSPITFTDISPLAYST7789::Clear(XWORD color)
00174 {
00175   int size = GetSizeBuffer();
00176 
00177   if(!TFT_SetWindow(0, 0, width, height)) return false;
00178 
00179   SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_RAMWR);  // write to RAM
00180 
00181   XBYTE chigh = (XBYTE)(color >> 8);
00182   XBYTE clow  = (XBYTE)(color &  0x00FF);
00183 
00184   canvasbuffer.Delete();
00185   canvasbuffer.Resize(size);
00186   canvasbuffer.ResetPosition();
00187 
00188   int c = 0;
00189  
00190   do{ canvasbuffer.Set(chigh);  c++;
00191       canvasbuffer.Set(clow);   c++;
00192 
00193     } while(c < size);
00194 
00195   return SendData();
00196 }
