00297 bool DIOSPITFTDISPLAYST7789::TFT_Init()
00298 {
00299   _colstart  = 0;
00300   _rowstart  = 0;
00301   _xstart    = 0;
00302   _ystart    = 0;
00303 
00304   //TFT_Reset();
00305 
00306   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_SWRESET)) return false;                         // Software reset
00307   GEN_XSLEEP.MilliSeconds(150);
00308 
00309   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_SLPOUT))  return false;                         // Out of sleep mode
00310   GEN_XSLEEP.MilliSeconds(500);
00311 
00312   if(!SendCommandParams(DIOSPITFTDISPLAYST7789_CMD_ST7789_COLMOD,  1, 0x55)) return false;          // Set color mode   0x55 (16-bit color)
00313   GEN_XSLEEP.MilliSeconds(10);
00314 
00315   if(!SendCommandParams(DIOSPITFTDISPLAYST7789_CMD_ST7789_MADCTL  , 1, 0x00)) return false;         // Memory access ctrl (directions) 0x00 Row addr/col addr, bottom to top refresh
00316 
00317   if(!SendCommandParams(DIOSPITFTDISPLAYST7789_CMD_ST7789_CASET   , 4                               // Column addr set
00318                                                                   , 0x00                            // XSTART = 0
00319                                                                   , DIOSPITFTDISPLAYST7789_240x240_XSTART
00320                                                                   , (DIOSPITFTDISPLAYST7789_TFTWIDTH + DIOSPITFTDISPLAYST7789_240x240_XSTART) >> 8
00321                                                                   , (DIOSPITFTDISPLAYST7789_TFTWIDTH + DIOSPITFTDISPLAYST7789_240x240_XSTART) & 0xFF)) return false;   // XEND = 240
00322   
00323   if(!SendCommandParams(DIOSPITFTDISPLAYST7789_CMD_ST7789_RASET   , 4                               // Row addr set
00324                                                                   , 0x00                            // YSTART = 0
00325                                                                   , DIOSPITFTDISPLAYST7789_240x240_YSTART
00326                                                                   , (DIOSPITFTDISPLAYST7789_TFTHEIGHT + DIOSPITFTDISPLAYST7789_240x240_YSTART) >> 8
00327                                                                   , (DIOSPITFTDISPLAYST7789_TFTHEIGHT + DIOSPITFTDISPLAYST7789_240x240_YSTART) & 0xFF)) return false;   // YEND = 240
00328 
00329 
00330 
00331   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_INVON)) return false;                           // Inversion ON
00332   GEN_XSLEEP.MilliSeconds(10);
00333 
00334   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_NORON)) return false;                           // Normal display on
00335   GEN_XSLEEP.MilliSeconds(10);
00336 
00337   if(!SendCommand(DIOSPITFTDISPLAYST7789_CMD_ST7789_DISPON)) return false;                          // Main display turn on, no args, w/delay
00338   GEN_XSLEEP.MilliSeconds(20);
00339 
00340   TFT_SetRotation(1); 
00341 
00342   return true;
00343 }
