00393 bool DIOSPITOUCHSCREENSTMPE610::TouchScreen_Ini()
00394 {
00395   if(!WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_SYS_CTRL1, DIOSPITOUCHSCREENSTMPE610_SYS_CTRL1_RESET)) return false;
00396 
00397   GEN_XSLEEP.MilliSeconds(250);
00398 
00399   for(XBYTE c=0; c<65; c++)
00400     {
00401       XBYTE data;
00402       ReadRegister(c, data);
00403     }
00404 
00405   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_SYS_CTRL2       , 0x0);                                                                             // turn on clocks!
00406   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_TSC_CTRL        , DIOSPITOUCHSCREENSTMPE610_TSC_CTRL_XYZ | DIOSPITOUCHSCREENSTMPE610_TSC_CTRL_EN);  // XYZ and enable!
00407   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_INT_EN          , DIOSPITOUCHSCREENSTMPE610_INT_EN_TOUCHDET);
00408   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_ADC_CTRL1       , DIOSPITOUCHSCREENSTMPE610_ADC_CTRL1_10BIT | (0x6 << 4));                          // 96 clocks per conversion
00409   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_ADC_CTRL2       , DIOSPITOUCHSCREENSTMPE610_ADC_CTRL2_6_5MHZ);
00410   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_TSC_CFG         , DIOSPITOUCHSCREENSTMPE610_TSC_CFG_4SAMPLE | DIOSPITOUCHSCREENSTMPE610_TSC_CFG_DELAY_1MS | DIOSPITOUCHSCREENSTMPE610_TSC_CFG_SETTLE_5MS);
00411   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_TSC_FRACT_XYZ   , 0x6);
00412   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_FIFO_TH         , 1);
00413   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_FIFO_STA        , DIOSPITOUCHSCREENSTMPE610_FIFO_STA_RESET);
00414   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_FIFO_STA        , 0);                                                                               // unreset
00415   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_TSC_I_DRIVE     , DIOSPITOUCHSCREENSTMPE610_TSC_I_DRIVE_50MA);
00416   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_INT_STA         , 0xFF);                                                                            // reset all ints
00417   WriteRegister(DIOSPITOUCHSCREENSTMPE610_REG_INT_CTRL        , DIOSPITOUCHSCREENSTMPE610_INT_CTRL_POL_HIGH | DIOSPITOUCHSCREENSTMPE610_INT_CTRL_ENABLE);
00418 
00419   return true;
00420 }
