00435 bool DIOSPITOUCHSCREENSTMPE610::ReadRegister(XBYTE reg, XBYTE& data)
00436 {
00437   //diostreamSPI->GetInXBuffer()->Delete();
00438 
00439   XBYTE regdata[2] = { (XBYTE)(reg | 0x80), 0 };
00440   XBYTE _data[2]   = { 0, 0 };
00441 
