00443   //if(status) status = diostreamSPI->WaitToFlushOutXBuffer(timeout);
00444 
00445   if(status) status = diostreamSPI->WaitToFilledReadingBuffer(2, timeout);
00446   if(status) status = diostreamSPI->Read(_data, 2)?true:false;
00447 
00448   data = _data[1];
00449 
00450   return status;
