00466 bool DIOSPITOUCHSCREENSTMPE610::WriteRegister(XBYTE reg, XBYTE data)
00467 {
00468   if(!databuffer) return false;
00469 
00470   bool status = diostreamSPI->Write(&reg, 1)?true:false;
00471   if(status) status = diostreamSPI->Write(&data, 1)?true:false;
00472   if(status) status = diostreamSPI->WaitToFlushOutXBuffer(timeout);
00473   if(status) status = diostreamSPI->WaitToFilledReadingBuffer(2, timeout);
00474 
00475   diostreamSPI->GetInXBuffer()->Delete();
00476 
00477   return status;
