00781 bool DIOSCRAPERWEB::ExtractString(XSTRING& searchini, XSTRING& searchend, XBUFFER* xbuffer, bool usebufferpos, XSTRING& result)
00782 {
00783   result.Empty();
00784 
00785   if(!xbuffer)            return false;
00786 
00787   if(searchini.IsEmpty()) return false;
00788   if(searchend.IsEmpty()) return false;
00789 
00790   XSTRING html;
00791   int        indexini;
00792   int        indexend;
00793 
00794   //html = (char*)xbuffer->Get();
00795   html = *xbuffer; // #Imanol, changed to avoid a buffer overrun
00796 
00797   indexini = html.Find(searchini,false,usebufferpos?xbuffer->GetPosition():0);
00798   if(indexini==XSTRING_NOTFOUND) return false;
00799 
00800   indexend = html.Find(searchend,false,indexini + searchini.GetSize());
00801   if(indexend==XSTRING_NOTFOUND) return false;
00802 
00803   if((int)(indexini + searchini.GetSize())>=indexend) return false;
00804 
00805   html.Copy(indexini + searchini.GetSize(),indexend,result);
00806 
00807   result.DeleteCharacter(__C(' '),XSTRINGCONTEXT_FROM_FIRST);
00808   result.DeleteCharacter(__C(' '),XSTRINGCONTEXT_TO_END);
00809 
00810   if(result.IsEmpty()) return false;
00811 
00812   return true;
00813 }
