00623 int DIOSTREAMDEVICEBLUETOOTH::GetChannel(XWORD servicetype)
00624 {
00625   for(int c=0; c<(int)services.GetSize(); c++)
00626     {
00627        DIOSTREAMDEVICEBLUETOOTHSDPSERVICE* service =  (DIOSTREAMDEVICEBLUETOOTHSDPSERVICE*)services.Get(c);
00628        if(service)
00629          {
00630            if(servicetype == service->GetID())
00631              {
00632                for(int d=0; d<(int)service->GetNProtocols(); d++)
00633                  {
00634                    DIOSTREAMDEVICEBLUETOOTHSDPSERVICEPROTOCOL* protocol = (DIOSTREAMDEVICEBLUETOOTHSDPSERVICEPROTOCOL*)service->GetProtocol(d);
00635                    if(protocol)
00636                      {
00637                        int channel = protocol->GetChannel();
00638                        if(channel) return channel;
00639                      }
00640                  }
00641             }
00642          }
00643     }
00644 
00645   return DIOSTREAMDEVICEBLUETOOTH_NOCHANNEL;
00646 }
