00707 int DIOSTREAMICMP::GetFirstDatagram(bool tosend)
00708 {
00709   int index = DIOSTREAMICMP_NOTFOUND;
00710 
00711   if(datagramsmutex)  datagramsmutex->Lock();
00712 
00713   for(int c=0;c<(int)datagrams.GetSize();c++)
00714     {
00715       DIOSTREAMICMPDATAGRAM* datagram = (DIOSTREAMICMPDATAGRAM*)datagrams.Get(c);
00716       if(datagram)
00717         {
00718           if(datagram->IsToSend() == tosend)
00719             {
00720               index = c;
00721               break;
00722             }
00723         }
00724     }
00725 
00726   if(datagramsmutex)  datagramsmutex->UnLock();
00727 
