00401 bool DIOSTREAMUDP::ReadDatagram(XSTRING& address, XWORD& port, XBUFFER& xbuffer)
00402 {
00403   int index = GetFirstDatagram(false);
00404   if(index != DIOSTREAMUDP_NOTFOUND)
00405     {
00406       DIOSTREAMUDPDATAGRAM* datagram = GetDatagram(index);
00407       if(datagram)
00408         {
00409           address = datagram->GetAddress()->Get();
00410           port    = datagram->GetPort();
00411 
00412           xbuffer.Delete();
00413           xbuffer.Add(datagram->GetData());
00414 
00415           nbytesread+= datagram->GetData()->GetSize();
00416           if(xtimernotactivity) xtimernotactivity->Reset();
00417 
00418           DeleteDatagram(index);
00419 
00420           return true;
00421         }
00422     }
00423 
00424   return false;
00425 }
