00717 bool DIOSTREAMUDP::DeleteDatagram(int index)
00718 {
00719   if(datagramsmutex)  datagramsmutex->Lock();
00720 
00721   DIOSTREAMUDPDATAGRAM* datagram = (DIOSTREAMUDPDATAGRAM*)GetDatagram(index);
