00831 int DIOSTREAMUDP::GetFirstDatagram(bool tosend)
00832 {
00833   int index = DIOSTREAMUDP_NOTFOUND;
00834 
00835   if(datagramsmutex)  datagramsmutex->Lock();
00836 
00837   for(int c=0;c<(int)datagrams.GetSize();c++)
00838     {
00839       DIOSTREAMUDPDATAGRAM* datagram = (DIOSTREAMUDPDATAGRAM*)datagrams.Get(c);
00840       if(datagram)
00841         {
00842           if(datagram->IsToSend() == tosend)
00843             {
00844               index = c;
00845               break;
00846             }
00847         }
00848     }
00849 
00850   if(datagramsmutex)  datagramsmutex->UnLock();
00851 
00852   return index;
00853 }
