00928 bool DIOURL::GetExtension(XSTRING& extension)
00929 {
00930   extension.Empty();
00931 
00932   int indexext;
00933   for(indexext = GetSize(); indexext>0; indexext--)
00934     {
00935       if(Get()[indexext] == __C('.'))
00936         {
00937           break;
00938         }
00939 
00940       if((Get()[indexext] == __C('\\')) ||
00941           Get()[indexext] == __C('/')   ||
00942           Get()[indexext] == __C(':'))
00943         {
00944           indexext = -1;
00945           break;
00946         }
00947     }
00948 
00949   if(indexext > 0) Copy(indexext, extension);
00950 
00951   return (extension.IsEmpty()?false:true);
00952 }
