00146 bool DIOWINDOWSSTREAMICMP::Open()
00147 {
00148   if(!threadconnection)  return false;
00149 
00150   if(!inbuffer)         return false;
00151   if(!outbuffer)        return false;
00152 
00153   if(!config->IsServer())
00154     {
00155       if(config->GetRemoteURL()->IsEmpty()) 
00156         {
00157           return false;   
00158         }
00159 
00160       if(config->GetRemoteURL()->IsAURL())
00161         {
00162           config->GetRemoteURL()->ResolveURL((*config->GetResolvedRemoteURL()));
00163         }
00164        else 
00165         {
00166           config->GetResolvedRemoteURL()->Set(config->GetRemoteURL()->Get());      
00167         }
00168     }
00169 
00170   SetEvent(DIOWINDOWSICMPFSMEVENT_GETTINGCONNECTION);
00171 
00172   status = DIOSTREAMSTATUS_GETTINGCONNECTION;
00173 
00174   ResetXBuffers();
00175 
00176   ResetConnectionStatistics();
00177 
00178   return threadconnection->Ini();
00179 }
