00191 bool DIOWINDOWSSTREAMICMP::Disconnect()
00192 {
00193   if((GetConnectStatus()==DIOSTREAMSTATUS_GETTINGCONNECTION)||
00194      (GetConnectStatus()==DIOSTREAMSTATUS_CONNECTED))
00195      {
00196        SetEvent(DIOWINDOWSICMPFSMEVENT_DISCONNECTING);
00197 
00198        while(GetConnectStatus()!=DIOSTREAMSTATUS_DISCONNECTED)
00199         {
00200           Sleep(10);
00201         }
00202      }
00203 
00204   return true;
00205 }
