00245 int DIOWINDOWSSTREAMICMP::IsReadyConnect(SOCKET socket)
00246 {
00247   struct timeval  tv;
00248   int             rc;
00249   fd_set          fdr;
00250   fd_set          fdw;
00251   fd_set          fds;
00252 
00253   if(socket == INVALID_SOCKET) return -1;
00254 
00255   FD_ZERO(&fdr);
00256   FD_ZERO(&fdw);
00257   FD_ZERO(&fds);
00258 
00259   FD_SET((unsigned int)socket, &fdr);
00260   FD_SET((unsigned int)socket, &fdw);
00261   FD_SET((unsigned int)socket, &fds);
00262 
00263   tv.tv_sec  = 0;
00264   tv.tv_usec = 100;
00265 
00266   rc = select((int)(socket)+1,&fdr, &fdw, &fds, &tv);
00267   if(rc == SOCKET_ERROR) return -1;
00268 
00269   int status1 = FD_ISSET(socket, &fdr) ? 1 : 0;
00270   int status2 = FD_ISSET(socket, &fdw) ? 1 : 0;
00271   int status3 = FD_ISSET(socket, &fds) ? 1 : 0;
00272 
00273   if(config->IsServer())
00274     {
00275       if(status1 || status2 )   return  1;
00276       if(status3)               return -1;
00277     }
00278    else
00279     {
00280       if((!status1) && status2) return  1;
00281       if(status3)               return -1;
00282     }
00283 
00284   return 0;
00285 }
