00765 
00766                                                                   if(esize)
00767                                                                     {
00768                                                                       diostream->outbuffer->SetBlocked(true);
00769                                                                       XDWORD size = send(diostream->handlesocket,(char*)diostream->outbuffer->Get(), esize,0);
00770                                                                       diostream->outbuffer->SetBlocked(false);
00771                                                                       if(size==SOCKET_ERROR)
00772                                                                         {
00773                                                                           diostream->SetEvent(DIOWINDOWSTCPIPFSMEVENT_DISCONNECTING);
00774                                                                           break;
00775                                                                         }
00776 
00777                                                                       if(size)
00778                                                                         {
00779                                                                           diostream->outbuffer->Extract(NULL, 0 ,size);
00780                                                                         }
00781                                                                     }
00782                                                                 }
00783                                                             }
00784                                                             break;
00785 
00786           case DIOWINDOWSTCPIPFSMSTATE_SENDINGDATA        : break                                                                                   ;
00787 
00788           case DIOWINDOWSTCPIPFSMSTATE_DISCONNECTING      : break;
00789 
00790         }
00791     }
00792    else
00793     {
00794       if(diostream->GetEvent()<DIOWINDOWSTCPIP_LASTEVENT)
00795         {
00796           diostream->CheckTransition();
00797 
00798           switch(diostream->GetCurrentState())
00799             {
00800               case DIOWINDOWSTCPIPFSMSTATE_NONE               : break;
00801 
00802               case DIOWINDOWSTCPIPFSMSTATE_GETTINGCONNECTION   : if(diostream->enumservers)
00803                                                                   {
00804                                                                     if(diostream->enumservers->GetXTimer()) diostream->enumservers->GetXTimer()->AddSeconds(DIOSTREAMTCPIP_TIMEOUTENUMSERVERSIGNAL);
00805                                                                   }
00806 
00807                                                                 if(diostream->config->IsServer())
00808                                                                   {
00809                                                                     diostream->config->SetCounterMultiServer(diostream->config->GetCounterMultiServer()+1);
00810                                                                   }
00811                                                                 break;
00812 
00813               case DIOWINDOWSTCPIPFSMSTATE_CONNECTED          : { DIOSTREAMXEVENT xevent(diostream, DIOSTREAMXEVENT_TYPE_CONNECTED);
00814                                                                   xevent.SetDIOStream(diostream);
00815                                                                   diostream->PostEvent(&xevent);
00816 
00817                                                                   diostream->SetEvent(DIOWINDOWSTCPIPFSMEVENT_WAITINGTOREAD);
00818                                                                 }
00819                                                                 break;
00820 
00821               case DIOWINDOWSTCPIPFSMSTATE_WAITINGTOREAD      : break;
00822 
00823               case DIOWINDOWSTCPIPFSMSTATE_SENDINGDATA        : break;
00824 
00825               case DIOWINDOWSTCPIPFSMSTATE_DISCONNECTING      : { DIOSTREAMXEVENT xevent(diostream, DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00826                                                                   xevent.SetDIOStream(diostream);
00827                                                                   diostream->PostEvent(&xevent);
00828 
00829                                                                   diostream->threadconnection->Run(false);
00830                                                                   diostream->status       = DIOSTREAMSTATUS_DISCONNECTED;
00831                                                                 }
00832                                                                 break;
00833 
00834             }
00835         }
00836     }
00837 }
