00333 XDWORD DIOWINDOWSSTREAMUART::ReadDirect(XBYTE* buffer,XDWORD size)
00334 {
00335   if(GetConnectStatus()==DIOSTREAMSTATUS_DISCONNECTED) return 0;
00336   if(size == 0)                                        return 0;
00337 
00338   DWORD br    = 0;
00339   bool status = true;
00340 
00341   if(!hevent)
00342     {
00343       hevent = CreateEvent(0,TRUE,FALSE,0);
00344       if(!hevent) return 0;
00345 
00346       ovi.hEvent = hevent;
00347 
00348       if(!ReadFile(hcom,buffer,size,&br,&ovi))
00349         {
00350           if(GetLastError()!=ERROR_IO_PENDING)
00351             {
00352               status = false;
00353             }
00354            else return ReadDirect(buffer, size);
00355         }
00356     }
00357    else
00358     {
00359       switch(WaitForSingleObject(ovi.hEvent,readtimeout))
00360         {
00361           case WAIT_OBJECT_0: if(!GetOverlappedResult(hcom,&ovi,&br,FALSE))
00362                                      status = false;
00363                                 else status = true;
00364                               break;
00365 
00366           case WAIT_TIMEOUT : CancelIo(hcom);
00367                               status=false;
00368                               break;
00369 
00370                    default  : status=false;
00371                               break;
00372 
00373          }
00374      }
00375 
00376   memset(&ovi,0,sizeof(ovi));
00377 
00378   if(hevent)
00379     {
00380       CloseHandle(hevent);
00381       hevent = NULL;
00382     }
00383 
00384   if(!status) return 0;
00385 
00386   return (XDWORD)br;
00387 }
