00402 XDWORD DIOWINDOWSSTREAMUART::WriteDirect(XBYTE* buffer, XDWORD size)
00403 {
00404   if(GetConnectStatus()==DIOSTREAMSTATUS_DISCONNECTED) return 0;
00405 
00406   if(size == 0)    return 0;
00407 
00408   DWORD      bw         = 0;
00409   OVERLAPPED oviwrite   = { 0 };
00410   bool       status;
00411 
00412   oviwrite.hEvent = CreateEvent(0,TRUE,FALSE,0);
00413   if(!oviwrite.hEvent) return bw;
00414 
00415   if(!WriteFile(hcom,buffer,size,&bw,&oviwrite))
00416     {
00417       if(GetLastError()!=ERROR_IO_PENDING)
00418         {
00419           status = false;
00420         }
00421        else
00422         {
00423           switch(WaitForSingleObject(oviwrite.hEvent,writetimeout))
00424             {
00425               case WAIT_OBJECT_0 :  if(!GetOverlappedResult(hcom,&oviwrite,&bw,TRUE))
00426                                            status = false;
00427                                       else status = true;
00428                                     break;
00429 
00430               case WAIT_TIMEOUT  : CancelIo(hcom);
00431                                    status=false;
00432                                    break;
00433 
00434                        default   : status=false;
00435                                    break;
00436 
00437             }
00438         }
00439 
00440     } else status = true;
00441 
00442   CloseHandle(oviwrite.hEvent);
00443 
00444   if(!status) return 0;
00445 
00446   return (XDWORD)bw;
00447 }
