00713 void DIOWINDOWSSTREAMUART::ThreadConnection(void* data)
00714 {
00715   DIOWINDOWSSTREAMUART* diostream = (DIOWINDOWSSTREAMUART*)data;
00716   if(!diostream) return;
00717 
00718   if(diostream->GetEvent()==DIOWINDOWSUARTFSMEVENT_NONE) // No hay nuevos Eventos
00719     {
00720       switch(diostream->GetCurrentState())
00721         {
00722           case DIOWINDOWSUARTFSMSTATE_NONE                  : break;
00723 
00724           case DIOWINDOWSUARTFSMSTATE_CONNECTED             : break;
00725 
00726           case DIOWINDOWSUARTFSMSTATE_WAITINGTOREAD         : { if(!diostream->IsBlockRead())
00727                                                                   {
00728                                                                     XBYTE buffer[DIOSTREAM_MAXBUFFER];
00729 
00730                                                                     XDWORD br = diostream->ReadDirect(buffer, DIOSTREAM_MAXBUFFER);
00731                                                                     if(br)
00732                                                                       {
00733                                                                         //XTRACE_PRINTDATABLOCK(XDEBUGLEVELCOLOR(2), (XBYTE*)diostream->buffer, (int)br);
00734                                                                         diostream->inbuffer->Add(buffer, br);
00735                                                                       }
00736                                                                   }
00737 
00738                                                                 if(!diostream->IsBlockWrite())
00739                                                                   {
00740                                                                     int esize = diostream->outbuffer->GetSize();
00741                                                                     if(esize)
00742                                                                       {
00743                                                                         diostream->outbuffer->SetBlocked(true);
00744                                                                         XDWORD bw = diostream->WriteDirect(diostream->outbuffer->Get(), esize);
00745                                                                         diostream->outbuffer->SetBlocked(false);
00746 
00747                                                                         if(bw) diostream->outbuffer->Extract(NULL, 0, bw);
00748 
00749                                                                       }
00750                                                                   }
00751                                                               }
00752                                                               break;
00753 
00754           case DIOWINDOWSUARTFSMSTATE_DISCONNECTING         : break;
00755 
00756         }
00757     }
00758    else
00759     {
00760       if(diostream->GetEvent()<DIOWINDOWSUART_LASTEVENT)
00761         {
00762           diostream->CheckTransition();
00763 
00764           switch(diostream->GetCurrentState())
00765             {
00766               case DIOWINDOWSUARTFSMSTATE_NONE              : break;
00767 
00768               case DIOWINDOWSUARTFSMSTATE_CONNECTED         : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_CONNECTED);
00769                                                                 xevent.SetDIOStream(diostream);
00770                                                                 diostream->PostEvent(&xevent);
00771 
00772                                                                 diostream->SetEvent(DIOWINDOWSUARTFSMEVENT_WAITINGTOREAD);
00773                                                               }
00774                                                               break;
00775 
00776               case DIOWINDOWSUARTFSMSTATE_WAITINGTOREAD     : break;
00777 
00778               case DIOWINDOWSUARTFSMSTATE_DISCONNECTING     : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00779                                                                 xevent.SetDIOStream(diostream);
00780 
00781                                                                 diostream->PostEvent(&xevent);
00782 
00783                                                                 diostream->threadconnection->Run(false);
00784                                                                 diostream->status       = DIOSTREAMSTATUS_DISCONNECTED;
00785                                                               }
00786                                                               break;
00787             }
00788         }
00789     }
00790 }
