00258 int DIOWINDOWSSTREAMUDP::IsReadyConnect(SOCKET socket)
00259 {
00260   struct timeval  tv;
00261   int             rc;
00262   fd_set          fdr;
00263   fd_set          fdw;
00264   fd_set          fds;
00265 
00266   if(socket == INVALID_SOCKET) return -1;
00267 
00268   FD_ZERO(&fdr);
00269   FD_ZERO(&fdw);
00270   FD_ZERO(&fds);
00271 
00272   FD_SET((unsigned int)socket, &fdr);
00273   FD_SET((unsigned int)socket, &fdw);
00274   FD_SET((unsigned int)socket, &fds);
00275 
00276   tv.tv_sec  = 0;
00277   tv.tv_usec = 100;
00278 
00279   rc = select((int)(socket)+1,&fdr, &fdw, &fds, &tv);
00280   if(rc == SOCKET_ERROR) return -1;
00281 
00282   int status1 = FD_ISSET(socket, &fdr) ? 1 : 0;
00283   int status2 = FD_ISSET(socket, &fdw) ? 1 : 0;
00284   int status3 = FD_ISSET(socket, &fds) ? 1 : 0;
00285 
00286   //XTRACE_PRINTCOLOR(1, __L("UDP stream: r: %d, w: %d, s: %d"), status1, status2, status3);
00287 
00288   if(config->IsServer())
00289     {
00290       if(status1 || status2 )   return  1;
00291       if(status3)               return -1;
00292     }
00293    else
00294     {
00295       if((!status1) && status2) return  1;
00296       if(status3)               return -1;
00297     }
00298 
00299   return 0;
00300 }
