00690                                                                 diostream->SetEvent(DIOWINDOWSUDPFSMEVENT_WAITINGTOREAD);
00691                                                               }
00692                                                               break;
00693 
00694               case DIOWINDOWSUDPFSMSTATE_WAITINGTOREAD      : break;
00695 
00696               case DIOWINDOWSUDPFSMSTATE_SENDINGDATA        : break;
00697 
00698               case DIOWINDOWSUDPFSMSTATE_DISCONNECTING      : { DIOSTREAMXEVENT xevent(diostream,DIOSTREAMXEVENT_TYPE_DISCONNECTED);
00699                                                                 xevent.SetDIOStream(diostream);
