00463 
00464       for(int c=0; c<(int)devices.GetSize(); c++)
00465         {
00466           DIOSTREAMDEVICEUSB* device1 = (DIOSTREAMDEVICEUSB*)devices.Get(c);
00467           if(device1)
00468             {
00469               for(int d=0; d<(int)devices.GetSize(); d++)
