00471                   DIOSTREAMDEVICEUSB* device2 = (DIOSTREAMDEVICEUSB*)devices.Get(d);
00472                   if(device2 && (c!=d))
00473                     {
00474                       if((!device1->GetLocation()->Compare(device2->GetLocation()->Get()) && (device1->GetClass() == device2->GetClass())))
00475                         {
00476                           devices.Delete(device1);
00477                           delete device1;
00478 
00479                           found = true;
00480                           break;
00481                         }
00482                     }
