00400 bool DIOWINDOWSSTREAMWIFIREMOTEENUMDEVICES::SetAllConnectionStatus(bool activate)
00401 {
00402   HANDLE                      handleclient        = NULL;
00403   DWORD                       max_client          = 2;
00404   DWORD                       current_version     = 0;
00405   DWORD                       result              = 0;  
00406   PWLAN_INTERFACE_INFO_LIST   interfacelist       = NULL;
00407   PWLAN_INTERFACE_INFO        interfacelinfo      = NULL;  
00408   PWLAN_INTERFACE_CAPABILITY  interfacecapability = NULL;
00409   
00410   result = WlanOpenHandle(max_client, NULL, &current_version, &handleclient);
00411   if(result != ERROR_SUCCESS) return false;
00412 
00413   result = WlanEnumInterfaces(handleclient, NULL, &interfacelist);
00414   if(result != ERROR_SUCCESS) return false;
00415 
00416 
00417   for(DWORD c=0; c<interfacelist->dwNumberOfItems; c++)
00418     {
00419       bool needactivated = false;
00420 
00421       interfacelinfo = (WLAN_INTERFACE_INFO*)&interfacelist->InterfaceInfo[c];
00422       
00423       switch(interfacelinfo->isState) 
00424         {
00425           case wlan_interface_state_not_ready             : break;  // Not ready
00426 
00427           case wlan_interface_state_connected             : break;  // Connected
00428 
00429           case wlan_interface_state_ad_hoc_network_formed : break;  // First node in a ad hoc network            
00430 
00431           case wlan_interface_state_disconnecting         : break;  // Disconnecting
00432 
00433           case wlan_interface_state_disconnected          : needactivated = true;
00434                                                             break;  // Not connected                
00435 
00436           case wlan_interface_state_associating           : break;  // Attempting to associate with a network
00437 
00438           case wlan_interface_state_discovering           : break;  // Auto configuration is discovering settings for the network
00439 
00440           case wlan_interface_state_authenticating        : break;  // In process of authenticating
00441 
00442                                           default         : break;  // Unknown state 
00443         }
00444 
00445       if(needactivated)
00446         {
00447           // get interface capability, which includes the supported PHYs   
00448           result = WlanGetInterfaceCapability(handleclient, &interfacelinfo->InterfaceGuid, NULL, &interfacecapability);
00449           if(result == ERROR_SUCCESS)
00450             {                
00451               // set radio state on every PHY   
00452               for(DWORD d=0; d<interfacecapability->dwNumberOfSupportedPhys; d++)
00453                 {
00454                   WLAN_PHY_RADIO_STATE  wlanphyradiostate;
00455                   
00456                   // set radio state on every PHY   
00457                   wlanphyradiostate.dwPhyIndex = d;
00458 
00459                   if(activate)    
00460                     {
00461                       wlanphyradiostate.dot11SoftwareRadioState = dot11_radio_state_on;    
00462                       wlanphyradiostate.dot11HardwareRadioState = dot11_radio_state_on;    
00463                     }                                    
00464                    else 
00465                     {
00466                       wlanphyradiostate.dot11SoftwareRadioState = dot11_radio_state_off;
00467                       wlanphyradiostate.dot11HardwareRadioState = dot11_radio_state_off;
00468                     }    
00469 
00470                   PVOID pdata = &wlanphyradiostate;
00471     
00472                   result = WlanSetInterface(handleclient, &interfacelinfo->InterfaceGuid, wlan_intf_opcode_radio_state, sizeof(WLAN_PHY_RADIO_STATE), pdata, NULL);
00473                   if(result == ERROR_SUCCESS)
00474                     {
00475                       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Wifi Activated.")); 
00476                     }
00477                    else
00478                     {
00479                       //XTRACE_PRINTCOLOR(XTRACE_COLOR_RED, __L("Wifi Activate Error!")); 
00480                     }    
00481                 }
00482             }         
00483         }
00484     }
00485   
