00544 void DIOWINDOWSSTREAMWIFIREMOTEENUMDEVICES::TriggerScan(HANDLE wlanhandle, WLAN_INTERFACE_INFO_LIST* interfaces)
00545 {
00546 
00547   for(ULONG i=0; i<interfaces->dwNumberOfItems; i++)
00548     {
00549       //Declare the callback parameter struct
00550       WLAN_CALLBACK_INFO callbackinfo;
00551 
00552       memset(&callbackinfo, 0, sizeof(WLAN_CALLBACK_INFO));
00553 
00554       callbackinfo.interfaceGUID = interfaces->InterfaceInfo[i].InterfaceGuid;
00555 
00556       //Create an event to be triggered in the scan case
00557       callbackinfo.scanevent = CreateEvent( NULL, FALSE, FALSE, NULL);
00558 
00559 
00560       //Register for wlan scan notifications
00561       WlanRegisterNotification(wlanhandle, WLAN_NOTIFICATION_SOURCE_ALL, TRUE, (WLAN_NOTIFICATION_CALLBACK)WLanCallback, (PVOID)&callbackinfo, NULL, NULL);
00562 
00563       //Start a scan. If the WlanScan call fails, log the error
00564       WlanScan(wlanhandle, &(interfaces->InterfaceInfo[i].InterfaceGuid), NULL, NULL, NULL);
00565       if(GetLastError() != ERROR_SUCCESS)
00566         {
00567           continue;
00568         }
00569 
00570       //Wait for the event to be signaled, or an error to occur. Don't wait longer than 15 seconds.
00571       DWORD waitresult = WaitForSingleObject(callbackinfo.scanevent, 15000);
00572 
00573       //Check how we got here, via callback or timeout
00574       if(waitresult == WAIT_OBJECT_0)
00575         {
00576           if(callbackinfo.callbackreason == wlan_notification_acm_scan_complete)
00577             {
00578 
00579             }
00580            else
00581             {
00582               if(callbackinfo.callbackreason == wlan_notification_acm_scan_fail)
00583                 {
00584 
00585                 }
00586             }
00587         }
00588        else
00589         {
00590           if(waitresult == WAIT_TIMEOUT)
00591             {
00592 
00593             }
00594            else
00595             {
00596 
00597             }
00598         }
00599     }
