00615 void WLanCallback(WLAN_NOTIFICATION_DATA *scannotificationdata, PVOID context)
00616 {
00617   // Get the data from my struct. If it's null, nothing to do
00618   WLAN_CALLBACK_INFO* callbackinfo = (WLAN_CALLBACK_INFO*)context;
00619   if(!callbackinfo)
00620     {
00621       return;
00622     }
00623 
00624   // Check the GUID in the struct against the GUID in the notification data, return if they don't match
00625   if(memcmp(&callbackinfo->interfaceGUID, &scannotificationdata->InterfaceGuid, sizeof(GUID)) != 0)
00626     {
00627       return;
00628     }
00629 
00630   // If the notification was for a scan complete or failure then we need to set the event
00631   if((scannotificationdata->NotificationCode == wlan_notification_acm_scan_complete) || (scannotificationdata->NotificationCode == wlan_notification_acm_scan_fail))
00632     {
00633       //Set the notification code as the callbackReason
00634       callbackinfo->callbackreason = scannotificationdata->NotificationCode;
00635 
00636       //Set the event
00637       SetEvent(callbackinfo->scanevent);
00638     }
00639 
00640   return;
00641 }
