01231       sendheader.AddFormat(__L("nonce=\"%s\", "), nonce_str.Get());           
01232       sendheader.AddFormat(__L("uri=\"%s\", "), resource.Get());     
01233       sendheader.AddFormat(__L("algorithm=%s, "), algorithm_str.Get());
01234       sendheader.AddFormat(__L("response=\"%s\", "), response.Get());   
01235       sendheader.AddFormat(__L("qop=%s, "), auth_str.Get());     
01236       sendheader.AddFormat(__L("nc=%s, "), nc_str.Get());     
01237       sendheader.AddFormat(__L("cnonce=\"%s\" "), cnonce_str.Get());   
01238       if(opaque_str.GetSize()) sendheader.AddFormat(__L(", opaque=\"%s\""), opaque_str.Get());           
01239       sendheader += __L("\r\n");
01240 
01241       sendheader += __L("\r\n");
01242       
01243       //XTRACE_PRINTCOLOR(XTRACE_COLOR_PURPLE, __L("%s"), sendheader.Get());
01244 
01245       diostream->WriteStr(sendheader);
01246 
01247       xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_WRITEHEADER);
01248       PostEvent(&xevent);
01249 
01250       //--- Send Post data -------------------------
01251 
01252       if(postdata)
01253         {
01254           diostream->Write(postdata->Get(), postdata->GetSize());
01255 
01256           xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_SENDPOSTDATA);
01257           PostEvent(&xevent);
01258         }
01259 
01260       diostream->WaitToFlushOutXBuffer(timeout);
01261 
01262       //--- Read Header ----------------------------
01263 
01264       if(!header.Read(diostream, timeout))
01265         {
01266           diostream->Close();
01267           return false;
01268         }
01269     }
01270 
01271 
01272   if((header.GetResultServer()>=400)||(!header.GetResultServer()))
01273     {     
01274       xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_HEADERERROR);
01275       PostEvent(&xevent);
01276 
01277       if(IsActiveDoStopHTTPError())
01278         {
01279           diostream->Close();
01280           return false;
01281         }
01282     }
01283 
01284   XQWORD   contentlength;
01285   XSTRING  etag;
01286 
01287   contentlength = header.GetContentLength();
01288   header.GetETag(etag);
01289 
01290   xevent.SetContentLenght(contentlength);
01291   xevent.GetEtag()->Set(etag);
01292 
01293   xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_READHEADER);
01294 
01295   PostEvent(&xevent);
01296 
01297   //--- Read Content ---------------------------
01298 
01299   XBUFFER* _tobuffer     = NULL;
01300   XFILE*   _tofile       = NULL;
01301  
01302   XTIMER* GEN_XFACTORY_CREATE(timerdownload, CreateTimer())
01303   if(!timerdownload)
01304     {     
01305       diostream->Close();
01306       return false;
01307     }
01308        
01309   if(istobuffer)
01310     {
01311       _tobuffer = (XBUFFER*)to;
01312       if(_tobuffer) _tobuffer->Delete();
01313     }
01314    else
01315     {
01316       _tofile = (XFILE*)to;
01317     }
01318 
01319   XBYTE* buffer = new XBYTE[maxsizebuffer];
01320   if(buffer)
01321     {
01322       XQWORD  totalsizeread = 0;
01323       int     br;
01324 
01325       timerout->Reset();
01326 
01327       do{ memset(buffer, 0, maxsizebuffer);
01328 
01329           br = diostream->Read(buffer, maxsizebuffer);
01330           if(br)
01331             {
01332               timerout->Reset();
01333 
01334               if(istobuffer)
01335                 {
01336                   if(_tobuffer) _tobuffer->Add(buffer, br);
01337                 }
01338                else
01339                 {
01340                   if(_tofile) _tofile->Write(buffer, br);
01341                 }
01342 
01343               totalsizeread += br;
01344 
01345               xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_READBODYBLOCK);
01346               xevent.SetDownloadSize(totalsizeread);
01347               xevent.SetContentLenght((XQWORD)contentlength);
01348 
01349               if(contentlength) xevent.SetOperationPercent((float)((totalsizeread * 100.0f) / contentlength));
01350 
01351               if(timerdownload->GetMeasureSeconds()) xevent.SetMBPSSpeed((XDWORD)(((totalsizeread * 8) / timerdownload->GetMeasureSeconds()) / 1000));
01352 
01353               PostEvent(&xevent);
01354             }
01355            else
01356             {
01357               if(timerout->GetMeasureSeconds()>(XDWORD)timeout)
01358                 {
01359                   if(!totalsizeread) status = false;
01360                   break;
01361                 }
01362 
01363               if(diostream->GetConnectStatus() != DIOSTREAMSTATUS_CONNECTED)
01364                 {
01365                   if(!diostream->GetInXBuffer()->GetSize())
01366                     {
01367                       break;
01368                     }
01369                 }
01370             }
01371 
01372         } while(1);
01373 
01374       delete [] buffer;
01375     }
01376 
01377   GEN_XFACTORY.DeleteTimer(timerdownload);
01378 
01379   //--- Close ----------------------------------
01380 
01381   diostream->Close();
01382 
01383   xevent.SetEventType(DIOWEBCLIENT_XEVENT_TYPE_CLOSEWEB);
01384   PostEvent(&xevent);
01385 
01386   return status;
01387 }
