01404 bool DIOWEBCLIENT::GetSubStringWWWWAuthenticate(XSTRING& www_authenticate, XCHAR* field, XSTRING& value, bool betweenquotation)
01405 {
01406   if(www_authenticate.IsEmpty()) return false;
01407 
01408   XSTRING sfield;
01409 
01410   sfield = field;
01411 
01412   int index = www_authenticate.Find(field, true);
01413   if(index == XSTRING_NOTFOUND) return false;
01414   index +=  sfield.GetSize();
01415 
01416   index = www_authenticate.Find(__L("="), true, index);
01417   if(index == XSTRING_NOTFOUND) return false;
01418 
01419   int start = 0;
01420   int end   = 0;
01421 
01422   if(betweenquotation)
01423     {
01424       start = www_authenticate.Find(__L("\""), true, index+1);
01425       if(start == XSTRING_NOTFOUND) return false;
01426 
01427       end = www_authenticate.Find(__L("\""), true, start+1);
01428       if(end == XSTRING_NOTFOUND) return false;
01429     }
01430    else
01431     { 
01432       start = index;
01433 
01434       end = www_authenticate.Find(__L(","), true, start+1);
01435       if(end == XSTRING_NOTFOUND) return false;
01436     }
01437 
01438   return www_authenticate.Copy(start+1, end, value)?true:false;
01439 }
