00801 bool DIOWEBPAGEHTMLCREATOR::AddFile(XPATH& xpath)
00802 {
00803   XFILETXT filetxt;
00804   bool     status = true;
00805   bool     intag  = false;
00806 
00807   if(!filetxt.Open(xpath)) return false;
00808 
00809   if(filetxt.ReadAllFile())
00810     {
00811       Empty();
00812 
00813       XBUFFER LFdata;
00814 
00815       filetxt.GetLF(LFdata);
00816 
00817       for(int c=0;c<filetxt.GetNLines();c++)
00818         {
00819           XSTRING* line = filetxt.GetLine(c);
00820           if(line)
00821             {
00822               XSTRING inioutput;
00823               XSTRING endoutput;
00824               int     result;
00825 
00826               result = CheckXTAG(line, intag, inioutput, endoutput);
00827               switch(result)
00828                 {
00829                   case DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_NOTFOUND   : { if(!line->IsEmpty())
00830                                                                               {
00831                                                                                 if(Add(*line)) Add(filetxt.GetLF()->Get());
00832                                                                               }
00833                                                                           }
00834                                                                           break;
00835 
00836                   case DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDEND   : { if(!inioutput.IsEmpty())
00837                                                                               {
00838                                                                                 if(Add(inioutput)) Add(filetxt.GetLF()->Get());
00839                                                                               }
00840 
00841                                                                             DIOWEBPAGEHTMLCREATOR_XEVENT xevent(this, DIOWEBPAGEHTMLCREATOR_XEVENT_TYPE_XTAGFOUND);
00842 
00843                                                                             xevent.SetPageHTMLCreator(this);
00844                                                                             xevent.SetXTAG(&lastXTAG);
00845 
00846                                                                             PostEvent(&xevent);
00847 
00848                                                                             if(!endoutput.IsEmpty())
00849                                                                               {
00850                                                                                 if(Add(endoutput)) Add(filetxt.GetLF()->Get());
00851                                                                               }
00852                                                                           }
00853                                                                           break;
00854 
00855                   case DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDNOTEND: { if(!inioutput.IsEmpty())
00856                                                                               {
00857                                                                                 if(Add(inioutput)) Add(filetxt.GetLF()->Get());
00858                                                                               }
00859 
00860                                                                             if(!endoutput.IsEmpty())
00861                                                                               {
00862                                                                                 if(Add(endoutput)) Add(filetxt.GetLF()->Get());
00863                                                                               }
00864                                                                           }
00865                                                                           break;
00866                 }
00867             }
00868         }
00869     }
00870 
00871   filetxt.Close();
00872 
