00891 DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS DIOWEBPAGEHTMLCREATOR::CheckXTAG(XSTRING* originalline,bool& intag, XSTRING& inioutputline,XSTRING& endoutputline)
00892 {
00893   int index[3];
00894 
00895   inioutputline.Empty();
00896   endoutputline.Empty();
00897 
00898   if(!intag)
00899     {
00900       index[0] = originalline->Find(__L("<?"), true);
00901       if(index[0]!=XSTRING_NOTFOUND)
00902         {
00903           index[1] = originalline->Find(DIOWEBPAGEHTMLCREATOR_TAGXENGINE, true);
00904           if(index[1]!=XSTRING_NOTFOUND)
00905             {
00906               lastXTAG.Empty();
00907 
00908               originalline->Copy(0,index[0],inioutputline);
00909 
00910               index[2] = originalline->Find(__L("?>"),true);
00911               if(index[2]!=XSTRING_NOTFOUND)
00912                 {
00913                   XSTRING endline;
00914 
00915                   originalline->Copy(index[1]+3,index[2],lastXTAG);
00916                   originalline->Copy(index[2]+2,endoutputline);
00917 
00918                   return DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDEND;
00919 
00920                 }
00921                else
00922                 {
00923                   originalline->Copy(index[1]+3,lastXTAG);
00924                   intag = true;
00925                   return  DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDNOTEND;
00926                 }
00927             }
00928         }
00929 
00930       inioutputline = originalline->Get();
00931     }
00932    else
00933     {
00934       index[0] = originalline->Find(__L("?>"),true);
00935       if(index[0]!=XSTRING_NOTFOUND)
00936         {
00937           XSTRING TAGstring;
00938 
00939           originalline->Copy(0,index[0],TAGstring);
00940           lastXTAG+=TAGstring.Get();
00941 
00942           originalline->Copy(index[0]+2,endoutputline);
00943 
00944           intag = false;
00945 
00946           return DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDEND;
00947         }
00948 
00949       lastXTAG += originalline->Get();
00950 
00951       inioutputline.Empty();
00952       endoutputline.Empty();
00953 
00954       return DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_FOUNDNOTEND;
00955     }
00956 
00957   return DIOWEBPAGEHTMLCREATORTAGXENGINESTATUS_NOTFOUND;
00958 }
