00427 GRPBITMAPFILE_TYPE GRPBITMAPFILE::GetTypeFromExtenxion(XCHAR* xpath)
00428 {
00429   XPATH               _xpath;
00430   XSTRING             ext;
00431   GRPBITMAPFILE_TYPE  type = GRPBITMAPFILE_TYPE_UNKNOWN;
00432 
00433   _xpath = xpath;
00434 
00435   _xpath.GetExt(ext);
00436   
00437   if(!ext.Compare(__L(".jpg"),  true))  type = GRPBITMAPFILE_TYPE_JPG; 
00438   if(!ext.Compare(__L(".jpeg"), true))  type = GRPBITMAPFILE_TYPE_JPG;
00439   if(!ext.Compare(__L(".png"),  true))  type = GRPBITMAPFILE_TYPE_PNG;
00440   if(!ext.Compare(__L(".bmp"),  true))  type = GRPBITMAPFILE_TYPE_BMP;
00441   if(!ext.Compare(__L(".tga"),  true))  type = GRPBITMAPFILE_TYPE_TGA;
00442 
00443   return type;
00444 }
