00656 GRPBITMAPSEQUENCE* GRPBITMAPFILE::LoadSequence(XCHAR* namemask, int inisequence, int endsequence)
00657 {
00658   if(xpath.IsEmpty()) return NULL;
00659 
00660   GRPBITMAPSEQUENCE* bitmapsequence = new GRPBITMAPSEQUENCE();
00661   if(!bitmapsequence) return NULL;
00662 
00663   XSTRING namefile;
00664 
00665   //xpath.GetNamefileExt(namefile);
00666   //if(!namefile.IsEmpty()) return NULL;
00667 
00668   // The sequence is in individual files in a directory. Sequence numbers must be continuos
00669   int nfiles = inisequence;
00670 
00671   while((nfiles < endsequence)|| (endsequence < 0))
00672     {
00673       XPATH xpathtemp;
00674       bool  existfile = false;
00675 
00676       namefile.Format(namemask, nfiles);
00677       xpathtemp.Create(2, xpath.Get(), namefile.Get());
00678 
00679       XFILE* GEN_XFACTORY_CREATE(xfile, Create_File())
00680       if(xfile)
00681         {
00682           existfile = xfile->Exist(xpathtemp);
00683           GEN_XFACTORY.Delete_File(xfile);
00684         }
00685 
00686       if(!existfile) break;
00687 
00688       GRPBITMAP* bitmap = Load(xpathtemp);
00689       if(!bitmap) break;
00690 
00691       bitmapsequence->AddFrame(bitmap);
00692 
00693       nfiles++;
00694     }
00695 
00696   return bitmapsequence;
00697 }
