00600 bool GRPBITMAPFILEJPG::EncodeToFile(int width, int height,  XBYTE* buffer, int quality, bool baseline)
00601 {
00602   if(!buffer)           return false;
00603   if(!GetPrimaryFile()) return false;
00604 
00605   struct jpeg_error_mgr jerr;
00606 
00607   JSAMPROW rowpointer[1]; /* pointer to JSAMPLE row[s] */
00608   int      rowstride;     /* physical row width in image buffer */
00609 
00610   GetPrimaryFile()->SetPosition(0);
00611 
00612   cinfo.err = jpeg_std_error(&jerr);
00613 
00614   jpeg_create_compress(&cinfo);
00615 
00616   FILE* filehandle = GetPrimaryFile()->CreateStructHandle();
00617   if(!filehandle) return false;
00618 
00619   jpeg_stdio_dest(&cinfo, filehandle);
00620 
00621   cinfo.image_width       = width;          /* image width and height, in pixels */
00622   cinfo.image_height      = height;
00623   cinfo.input_components  = 3;              /* # of color components per pixel */
00624   cinfo.in_color_space    = JCS_RGB;        /* colorspace of input image */
00625 
00626   jpeg_set_defaults(&cinfo);
00627 
00628   jpeg_set_quality(&cinfo, quality, baseline);
00629 
00630   jpeg_start_compress(&cinfo, TRUE);
00631 
00632   rowstride = width * 3;  /* JSAMPLEs per row in image_buffer */
00633 
00634   int size  = width * height * 3;
00635 
00636   size -= rowstride;
00637 
00638   while (cinfo.next_scanline < cinfo.image_height)
00639     {
00640       rowpointer[0] = &buffer[size-(cinfo.next_scanline * rowstride)];
00641 
00642       jpeg_write_scanlines(&cinfo, rowpointer, 1);
00643     }
00644 
00645   jpeg_finish_compress(&cinfo);
00646 
00647   jpeg_destroy_compress(&cinfo);
00648 
00649   GetPrimaryFile()->SetPosition(0);
00650 
00651   return true;
00652 }
