00107 bool GRPBITMAPFILETGAHEADER::Load(XFILE* file)
00108 {
00109   if(!file->Read((XBYTE*)&headerinfo.IDlength         , sizeof(XBYTE))) return false;
00110   if(!file->Read((XBYTE*)&headerinfo.colourmaptype    , sizeof(XBYTE))) return false;
00111   if(!file->Read((XBYTE*)&headerinfo.datatypecode     , sizeof(XBYTE))) return false;
00112   if(!file->Read((XBYTE*)&headerinfo.colourmaporigin  , sizeof(XWORD))) return false;
00113   if(!file->Read((XBYTE*)&headerinfo.colourmaplength  , sizeof(XWORD))) return false;
00114   if(!file->Read((XBYTE*)&headerinfo.colourmapdepth   , sizeof(XBYTE))) return false;
00115   if(!file->Read((XBYTE*)&headerinfo.xorigin          , sizeof(XWORD))) return false;
00116   if(!file->Read((XBYTE*)&headerinfo.yorigin          , sizeof(XWORD))) return false;
00117   if(!file->Read((XBYTE*)&headerinfo.width            , sizeof(XWORD))) return false;
00118   if(!file->Read((XBYTE*)&headerinfo.height           , sizeof(XWORD))) return false;
00119   if(!file->Read((XBYTE*)&headerinfo.bitsperpixel     , sizeof(XBYTE))) return false;
00120   if(!file->Read((XBYTE*)&headerinfo.imagedescriptor  , sizeof(XBYTE))) return false;
00121 
00122   // Only RGB format valid.
00123   if((headerinfo.datatypecode != GRPBITMAPFILETGATYPE_UNCOMPRESS_RGB) &&
00124      (headerinfo.datatypecode != GRPBITMAPFILETGATYPE_RLECOMPRESS_RGB))  return false;
00125 
00126   // Only 16,24,32 bits format valid.
00127   if((headerinfo.bitsperpixel != 16) &&
00128      (headerinfo.bitsperpixel != 24) &&
00129      (headerinfo.bitsperpixel != 32)) return false;
00130 
00131   // Only 0,1 colour map types
00132   if((headerinfo.colourmaptype != 0) && (headerinfo.colourmaptype != 1)) return false;
00133 
00134   switch(headerinfo.bitsperpixel)
00135     {
00136       case 16 : mode = GRPPROPERTYMODE_16_RGBA_5551;  break;
00137       case 24 : mode = GRPPROPERTYMODE_24_RGB_888;    break;
00138       case 32 : mode = GRPPROPERTYMODE_32_RGBA_8888;  break;
00139     }
00140 
00141   offsetdata += sizeof(GRPBITMAPFILETGAHEADERINFO);
00142   offsetdata += headerinfo.IDlength;
00143   offsetdata += headerinfo.colourmaptype * headerinfo.colourmaplength;
00144 
00145   return true;
00146 }
