00677 bool GRPBITMAPFILETGA::CreateFileFromBitmap(XPATH& xpath, GRPBITMAP* bitmap, int quality)
00678 {
00679   if(!bitmap)         return false;
00680 
00681   if(!Create(xpath))  return false;
00682 
00683   GRPBITMAPFILETGAHEADER header;
00684 
00685   header.GetHeaderInfo()->IDlength            = 0;
00686   header.GetHeaderInfo()->colourmaptype       = 0;
00687   header.GetHeaderInfo()->datatypecode        = GRPBITMAPFILETGATYPE_UNCOMPRESS_RGB;
00688   header.GetHeaderInfo()->colourmaporigin     = 0;
00689   header.GetHeaderInfo()->colourmaplength     = 0;
00690   header.GetHeaderInfo()->colourmapdepth      = 0;
00691   header.GetHeaderInfo()->xorigin             = 0;
00692   header.GetHeaderInfo()->yorigin             = 0;
00693   header.GetHeaderInfo()->width               = bitmap->GetWidth();
00694   header.GetHeaderInfo()->height              = bitmap->GetHeight();
00695   header.GetHeaderInfo()->bitsperpixel        = 32;
00696   header.GetHeaderInfo()->imagedescriptor     = 8;
00697 
00698   int  width        = bitmap->GetWidth();
00699   int  height       = bitmap->GetHeight();
00700   int  bytesppixel  = (header.GetBitsPerPixel()/8);
00701   int  sizebmp      = (header.GetWidth()*header.GetHeight()*bytesppixel);
00702   int  index        = 0;
00703   bool status       = true;
00704 
00705   if(file->Write((XBYTE*)header.GetHeaderInfo(), sizeof(GRPBITMAPFILETGAHEADERINFO)))
00706     {
00707       XBYTE* databuffer = new XBYTE[sizebmp];
00708       if(databuffer)
00709         {
00710           for(int y=height-1; y>-1; y--)
00711             {
00712               for(int x=0; x<width; x++)
00713                 {
00714                   GRP2DCOLOR_RGBA8* color = (GRP2DCOLOR_RGBA8*)bitmap->GetPixel(x, y);
00715                   if(color)
00716                     {
00717                       databuffer[index+2] = color->r;
00718                       databuffer[index+1] = color->g;
00719                       databuffer[index+0] = color->b;
00720                       databuffer[index+3] = color->a;
00721                     }
00722 
00723                   index  += bytesppixel;
00724                 }
00725             }
00726 
00727           if(!file->Write((XBYTE*)databuffer, sizebmp)) status = false;
00728 
00729           delete [] databuffer;
00730 
00731         } else status = false;
00732 
00733     } else status = false;
00734 
00735   Close();
00736 
00737   return status;
00738 }
