00797 bool GRPBITMAPFILETGA::CreateColor(GRPPROPERTYMODE mode, XBYTE* databuffer, GRP2DCOLOR_RGBA8& color)
00798 {
00799   if(!databuffer) return false;
00800 
00801   switch(mode)
00802     {
00803       case GRPPROPERTYMODE_16_RGBA_5551 : color.r =  (databuffer[1] & 0x7c) << 1;
00804                                           color.g = ((databuffer[1] & 0x03) << 6) | ((databuffer[0] & 0xe0) >> 2);
00805                                           color.b =  (databuffer[0] & 0x1f) << 3;
00806                                           color.a =  (databuffer[1] & 0x80)?0:255;
00807                                           break;
00808 
00809       case GRPPROPERTYMODE_24_RGB_888   : color.r = databuffer[2];
00810                                           color.g = databuffer[1];
00811                                           color.b = databuffer[0];
00812                                           color.a = 255;
00813                                           break;
00814 
00815       case GRPPROPERTYMODE_32_RGBA_8888 : color.r = databuffer[2];
00816                                           color.g = databuffer[1];
00817                                           color.b = databuffer[0];
00818                                           color.a = databuffer[3];
00819                                           break;
00820       default: break;
00821     }
00822 
00823   return true;
00824 }
