00167                                                                                   m_num_vertices = 3;
00168                                                                                   m_count        = 0;
00169 
00170                                                                                   m_x[0] = x1;
00171                                                                                   m_y[0] = y1;
00172                                                                                   m_x[1] = x2;
00173                                                                                   m_y[1] = y2;
00174                                                                                   m_x[2] = x3;
00175                                                                                   m_y[2] = y3;
00176 
00177                                                                                   m_x[3] = m_y[3] = m_x[4] = m_y[4] = 0.0;
00178 
00179                                                                                   m_cmd[0] = agg::path_cmd_move_to;
00180                                                                                   m_cmd[1] = agg::path_cmd_line_to;
