00582     void                                                                        RoundRect                         (double x1, double y1, double x2, double y2, double radius, bool isfill = false)
00583                                                                                 {
00584                                                                                   if(isfill)
00585                                                                                     {
00586                                                                                       AGG_SOLIDFILL_INI
00587 
00588                                                                                       ren.color(renderer_primitives->fill_color());
00589 
00590                                                                                       ras.gamma(agg::gamma_power(0.0f));
00591 
00592                                                                                       agg::rounded_rect roundrect(x1, y1, x2, y2, radius);
00593                                                                                       ras.add_path(roundrect);
00594 
00595                                                                                       AGG_SOLIDFILL_END
00596                                                                                     }
00597 
00598                                                                                   
00599                                                                                   if(linewidth <= 1.0)
00600                                                                                     {
00601                                                                                       //renderer_primitives->rectangle((int)x1, (int)y1, (int)x2, (int)y2);
00602                                                                                     }
00603                                                                                    else
00604                                                                                     {
00605                                                                                       AGG_OUTLINE_INI
00606 
00607                                                                                       ren.color(renderer_primitives->line_color());
00608 
00609                                                                                       //ras.round_cap(true);                 //optional
00610                                                                                       //ras.accurate_join(true);             //optional
00611 
00612                                                                                       agg::rounded_rect roundrect(x1, y1, x2, y2, radius);
00613                                                                                       ras.add_path(roundrect);
00614 
00615                                                                                       AGG_OUTLINE_END
00616 
00617                                                                                     }
00618                                                                                 }
