00620 
00621     GRPBITMAP*                                                                  GetBitmap                         (double x, double y, double width, double height)
00622                                                                                 {
00623 
00624                                                                                   //Rectangle(x, y, x+width, y+height, false);
00625 
00626                                                                                   int clipwidth    = (renderer_base->xmax() - renderer_base->xmin());
00627                                                                                   int clipheight   = (renderer_base->ymax() - renderer_base->ymin());
00628                                                                                   int bitmapwidth  = (int)width;
00629                                                                                   int bitmapheight = (int)height;
00630                                                                                   int xpos         = (int)(x * GetBytesperPixel());
00631                                                                                   int c            = 0;
00632 
00633                                                                                   if(x < 0)
00634                                                                                     {
00635                                                                                       xpos         = 0;
00636                                                                                       bitmapwidth  = (int)(bitmapwidth  + x);
00637                                                                                     }
00638 
00639                                                                                   if(y < 0)
00640                                                                                     {
00641                                                                                       c = abs((int)y);
00642                                                                                     }
00643 
00644                                                                                   if((x + width)  > (renderer_base->xmin() + clipwidth))
00645                                                                                     {
00646                                                                                       bitmapwidth  = (int)(((clipwidth + renderer_base->xmin()) - x) + 1);
00647                                                                                     }
00648 
00649                                                                                   if((y + height) > (renderer_base->ymin() + clipheight))
00650                                                                                     {
00651                                                                                       bitmapheight = ((clipheight + renderer_base->ymin()) - (int)y) + 1;
00652                                                                                     }
00653 
00654                                                                                   if(bitmapwidth  <= 0) return NULL;
00655                                                                                   if(bitmapheight <= 0) return NULL;
00656 
00657 
00658 
00659                                                                                   GRPBITMAP* bitmap = GRPFACTORY::GetInstance().CreateBitmap(bitmapwidth, bitmapheight, mode, isbufferinverse);
00660                                                                                   if(!bitmap) return NULL;
00661 
00662 
00663                                                                                   GRPBITMAPPIXELFORMATBUFFER<PIXELFORMATBUFFER, COLORTYPE>* bitmapfb = (GRPBITMAPPIXELFORMATBUFFER<PIXELFORMATBUFFER, COLORTYPE>*)bitmap;
00664                                                                                   agg::rendering_buffer* bitmapbuffer = bitmapfb->GetRenderBuffer();
00665 
00666 
00667                                                                                   int sizeline = bitmapwidth *  GetBytesperPixel();
00668 
00669                                                                                   while(c < bitmapheight)
00670                                                                                     {
00671                                                                                       memcpy(bitmapbuffer->row_ptr(c), (rbuffer.row_ptr(c + (int)y) + xpos), sizeline);
00672                                                                                       c++;
00673                                                                                     }
00674 
00675                                                                                   return bitmap;
00676                                                                                 }
00677 
00678 
00679     void                                                                        PutBitmapNoAlpha                  (double x, double y, GRPBITMAP* bitmap)
00680                                                                                 {
00681                                                                                   if(!bitmap) return;
00682 
00683                                                                                   if(bitmap->GetMode() != GetMode())
00684                                                                                     {
00685                                                                                       COLORTYPE* color;
00686 
00687                                                                                       for(XDWORD yb = 0; yb<bitmap->GetHeight(); yb++)
00688                                                                                         {
00689                                                                                           for(XDWORD xb = 0; xb<bitmap->GetWidth(); xb++)
00690                                                                                             {
00691                                                                                               color = (COLORTYPE*)bitmap->GetPixel(xb,yb);
00692                                                                                               PutPixel(x+xb, y+yb, color);
00693                                                                                             }
00694                                                                                         }
00695                                                                                     }
00696                                                                                    else
00697                                                                                     {
00698                                                                                       int xslide       = 0;
00699                                                                                       //int yslide       = 0;
00700                                                                                       int clipwidth    = (renderer_base->xmax() - renderer_base->xmin());
00701                                                                                       int clipheight   = (renderer_base->ymax() - renderer_base->ymin());
00702                                                                                       int bitmapwidth  = bitmap->GetWidth();
00703                                                                                       int bitmapheight = bitmap->GetHeight();
00704                                                                                       int xpos         = (int)(abs((int)x) * GetBytesperPixel());
00705                                                                                       int c            = 0;
00706 
00707 
00708                                                                                       if(x < 0)
00709                                                                                         {
00710                                                                                           xpos    = (int)0;
00711                                                                                           xslide  = (int)(abs((int)x) * GetBytesperPixel());
00712                                                                                         }
00713 
00714                                                                                       if(y < 0)
00715                                                                                         {
00716                                                                                           c = (int)abs((int)y);
00717                                                                                         }
00718 
00719 
00720                                                                                       if((x + (int)bitmap->GetWidth())  > (renderer_base->xmin() + clipwidth))
00721                                                                                         {
00722                                                                                           bitmapwidth  = (int)((clipwidth +renderer_base->xmin()) - x) + 1;
00723                                                                                         }
00724 
00725                                                                                       if((y + (int)bitmap->GetHeight()) > (renderer_base->ymin() + clipheight))
00726                                                                                         {
00727                                                                                           bitmapheight = (int)((clipheight + renderer_base->ymin()) - y) + 1;
00728                                                                                         }
00729 
00730                                                                                       if(bitmapwidth  <= 0) return;
00731                                                                                       if(bitmapheight <= 0) return;
00732 
00733                                                                                       GRPBITMAPPIXELFORMATBUFFER<PIXELFORMATBUFFER, COLORTYPE>* bitmapfb = (GRPBITMAPPIXELFORMATBUFFER<PIXELFORMATBUFFER, COLORTYPE>*)bitmap;
00734                                                                                       agg::rendering_buffer* bitmapbuffer = bitmapfb->GetRenderBuffer();
00735 
00736                                                                                       int nlines    = bitmapheight;
00737                                                                                       int sizeline  = bitmapwidth * GetBytesperPixel();
00738 
00739                                                                                       while(c < nlines)
00740                                                                                         {
00741                                                                                           memcpy(rbuffer.row_ptr((int)(c + y)) + xpos, bitmapbuffer->row_ptr(c) + xslide, sizeline);
00742                                                                                           c++;
00743                                                                                         }
00744                                                                                     }
00745 
00746                                                                                   #ifdef GEN_DEBUG 
00747                                                                                   //Debug_Draw(x, y, bitmap->GetWidth(), bitmap->GetHeight());
00748                                                                                   #endif  
00749                                                                                 }
