00752     void                                                                        PutBitmap                         (double x, double y, GRPBITMAP* bitmap)
00753                                                                                 {
00754                                                                                   if(!bitmap) return;
00755 
00756                                                                                   COLORTYPE* color;
00757 
00758                                                                                   for(XDWORD yb = 0; yb<bitmap->GetHeight(); yb++)
00759                                                                                     {
