00850 
00851                                                                                       if(!strfontname.Compare(_strfontname))
00852                                                                                         {
00853                                                                                           rasterglyph->font(fonts[c].font);
00854                                                                                           break;
00855                                                                                         }
00856                                                                                     }
00857 
00858                                                                                   return true;
00859                                                                                 }
