00897     bool                                                                        VectorFont_Load                   (XPATH& fontnamefile)
00898                                                                                 {                                      
00899                                                                                   agg::glyph_rendering gren     = agg::glyph_ren_native_mono;
00900                                                                                   bool                 status   = false;
00901                                                                                   int                  mode     = 1;   
00902 
00903                                                                                   switch(mode)                                                                                          
00904                                                                                     {
00905                                                                                       default :        
00906                                                                                       case  0 : gren = agg::glyph_ren_native_mono;  break;
00907                                                                                       case  1 : gren = agg::glyph_ren_native_gray8; break;
00908                                                                                       case  2 : gren = agg::glyph_ren_outline;      break;
00909                                                                                       case  3 : gren = agg::glyph_ren_agg_mono;     break;
00910                                                                                       case  4 : gren = agg::glyph_ren_agg_gray8;    break;
00911                                                                                     }
00912 
00913                                                                                   XBUFFER charstr;
00914                                                                                   
00915                                                                                   fontnamefile.ConvertToASCII(charstr);                                                                                 
00916                                                                                   status = vectorfont_engine.load_font(charstr.GetPtrChar(), 0, gren);
00917                                                                                   
00918                                                                                   return status;    
00919                                                                                 }        
00920                                               
00921     double                                                                      VectorFont_GetWidth               (XCHAR* string)
00922                                                                                 {
00923                                                                                   double x        = 0;
00924                                                                                   double y        = 0;
00925                                                                                   double _width   = 0;   
00926 
00927                                                                                   XSTRING _string;
00928 
00929                                                                                   _string = string;
00930 
00931                                                                                   AGG_SOLIDFILL_INI  
00932 
00933                                                                                   vectorfont_engine.hinting(true);
00934                                                                                   vectorfont_engine.height(vectorfont_config.GetHeight());
00935                                                                                   vectorfont_engine.width(vectorfont_config.GetWidth());
00936                                                                                   vectorfont_engine.flip_y(true);
00937                                                                                                                                                                  
00938                                                                                   XBUFFER charstr;
00939                                                                                   
00940                                                                                   _string.ConvertToASCII(charstr);
00941                                                                                                                                                                     
00942                                                                                   const  char* p = charstr.GetPtrChar();
00943 
00944                                                                                   for(XDWORD c=0; c<_string.GetSize(); c++)
00945                                                                                   //while(*p)
00946                                                                                     {
00947                                                                                       const agg::glyph_cache* glyph = vectorfont_manager->glyph(*p);
00948 
00949                                                                                       if(glyph)
00950                                                                                         {
00951                                                                                           if(vectorfont_config.IsKerning()) 
00952                                                                                             {
00953                                                                                               vectorfont_manager->add_kerning(&x, &y);
00954                                                                                             }
00955                                                                                                                                                                               
00956                                                                                           vectorfont_manager->init_embedded_adaptors(glyph, x, y);   
00957                                                                                           _width += glyph->advance_x;   
00958                                                                                         }
00959                                                                                                                                                                                                                                                             
00960                                                                                       ++p;
00961                                                                                     }
00962 
00963                                                                                   AGG_SOLIDFILL_END
00964                                                                                   
00965                                                                                   return _width;                      
00966                                                                                 }
