00968  double                                                                         VectorFont_GetHeight              (XCHAR* string)
00969                                                                                 {
00970                                                                                   double x        = 0;
00971                                                                                   double y        = 0;
00972                                                                                   double _height  = 0;   
00973 
00974                                                                                   XSTRING _string;
00975 
00976                                                                                   _string = string;
00977 
00978                                                                                   AGG_SOLIDFILL_INI  
00979 
00980                                                                                   vectorfont_engine.hinting(true);
00981                                                                                   vectorfont_engine.height(vectorfont_config.GetHeight());
00982                                                                                   vectorfont_engine.width(vectorfont_config.GetWidth());
00983                                                                                   vectorfont_engine.flip_y(true);
00984                                                                                 
00985                                                                                   XBUFFER charstr;
00986                                                                                   
00987                                                                                   _string.ConvertToASCII(charstr);
00988                                                                                                                                                                   
00989                                                                                   const char* p = charstr.GetPtrChar();
00990 
00991                                                                                   for(XDWORD c=0; c<_string.GetSize(); c++)
00992                                                                                   //while(*p)
00993                                                                                     {
00994                                                                                       const agg::glyph_cache* glyph = vectorfont_manager->glyph(*p);
00995 
00996                                                                                       if(glyph)
00997                                                                                         {
00998                                                                                           if(vectorfont_config.IsKerning()) 
00999                                                                                             {
01000                                                                                               vectorfont_manager->add_kerning(&x, &y);
01001                                                                                             }
01002                                                                                                                                                                               
01003                                                                                           vectorfont_manager->init_embedded_adaptors(glyph, x, y);   
01004 
01005                                                                                           if((*p) != ' ')
01006                                                                                             {
01007                                                                                               int glyph_height = abs(glyph->bounds.y2-glyph->bounds.y1);  
01008 
01009                                                                                               if( glyph_height > _height) _height = glyph_height;   
01010                                                                                             }
01011                                                                                         }
01012                                                                                                                                                                                                                                                             
01013                                                                                       ++p;
01014                                                                                     }
01015 
01016                                                                                   AGG_SOLIDFILL_END
01017 
01018                                                                                   return _height;                      
01019                                                                                 }
