01027 
01028     bool                                                                        VectorFont_Print                  (double _x, double _y, XCHAR* _outstring)
01029                                                                                 {
01030                                                                                   XSTRING outstring;
01031                                                                                   
01032                                                                                   outstring = _outstring;
01033                                                                                   
01034                                                                                   XDWORD  num_glyphs = 0;
01035                                                                                   
01036                                                                                   AGG_SOLIDFILL_INI  
01037 
01038                                                                                   vectorfont_engine.hinting(true);
01039                                                                                   vectorfont_engine.height(vectorfont_config.GetHeight());
01040                                                                                   vectorfont_engine.width(vectorfont_config.GetWidth());
01041                                                                                   vectorfont_engine.flip_y(true); 
01042                                                                                   
01043                                                                                   double x      = _x;
01044                                                                                   double y      = _y+1;
01045                                                                                   
01046                                                                                   for(XDWORD c=0; c<outstring.GetSize(); c++)
01047                                                                                     {
01048                                                                                       XCHAR character = outstring.Get()[c];
01049 
01050                                                                                       const agg::glyph_cache* glyph = vectorfont_manager->glyph(character);
01051 
01052                                                                                       if(glyph)
01053                                                                                         {
01054                                                                                           if(vectorfont_config.IsKerning()) 
01055                                                                                             {
01056                                                                                               vectorfont_manager->add_kerning(&x, &y);
01057                                                                                             }
01058                                                                                                                                                                               
01059                                                                                           vectorfont_manager->init_embedded_adaptors(glyph, x, y);
01060 
01061                                                                                           switch(glyph->data_type)
01062                                                                                             {
01063                                                                                                                   default      : break;
01064 
01065                                                                                               case agg::glyph_data_mono       : ren_bin.color((*vectorfont_config.GetColor()));
01066                                                                                                                                 agg::render_scanlines(vectorfont_manager->mono_adaptor(), vectorfont_manager->mono_scanline(), ren_bin);
01067                                                                                                                                 break;
01068 
01069                                                                                               case agg::glyph_data_gray8      : ren.color((*vectorfont_config.GetColor()));
01070                                                                                                                                 agg::render_scanlines(vectorfont_manager->gray8_adaptor(), vectorfont_manager->gray8_scanline(), ren);
01071                                                                                                                                 break;
01072 
01073                                                                                               case agg::glyph_data_outline    : ras.reset();
01074 
01075                                                                                                                                 
01076                                                                                                                                 /* 
01077                                                                                                                                 if(fabs(m_weight.value()) <= 0.01)
01078                                                                                                                                   {
01079                                                                                                                                     // For the sake of efficiency skip the
01080                                                                                                                                     // contour converter if the weight is about zero.
01081                                                                                                                                     //-----------------------
01082 
01083                                                                                                                                     ras.add_path(m_curves);
01084                                                                                                                                   }
01085                                                                                                                                   else
01086                                                                                                                                   {
01087                                                                                                                                     ras.add_path(m_contour);
01088                                                                                                                                   }
01089                                                                                                                                 */
01090                                                                                                                                 renderer_scanline->color((*vectorfont_config.GetColor()));
01091                                                                                                                                 agg::render_scanlines(ras, sl, ren);                                                                                  
01092                                                                                                                                 break;
01093                                                                                               }
01094 
01095                                                                                             // increment pen position
01096                                                                                             x += glyph->advance_x;
01097                                                                                             y += glyph->advance_y;  
01098 
01099                                                                                             ++num_glyphs;
01100                                                                                           }                                                                                        
01101                                                                                     }
01102 
01103                                                                                   AGG_SOLIDFILL_END                                                                                  
01104                                                                               
01105                                                                                   return true;
01106                                                                                 }
