01126 
01127     bool                                                                        CopyBufferRenderToScreen          (GRPSCREEN* screen)
01128                                                                                 {
01129                                                                                   if(!screen) return false;
01130 
01131                                                                                   GRPRECTINT* rectscreen  = GetScreenZone();
01132                                                                                   if(!rectscreen) return false;
01133 
01134                                                                                   agg::rendering_buffer screenbuffer;
01135 
01136                                                                                   screenbuffer.attach(screen->GetBuffer(), screen->GetWidth()
01137                                                                                                                          , screen->GetHeight()
01138                                                                                                                          , (screen->IsBufferInverse()?1:-1) * ((int)screen->GetWidth() * screen->GetBytesperPixel()));
01139 
01140                                                                                   int nlines    = (rectscreen->y2-rectscreen->y1);
01141                                                                                   int sizeline  = (rectscreen->x2-rectscreen->x1) * GetBytesperPixel();
01142                                                                                   int xpos      = (rectscreen->x1 * GetBytesperPixel());
01143 
01144                                                                                   int c         = 0;
01145 
01146                                                                                   while(c < nlines)
01147                                                                                     {
01148                                                                                       memcpy(screenbuffer.row_ptr(c), (rbuffer.row_ptr(rectscreen->y1+c) + xpos), sizeline);
01149                                                                                       c++;
01150                                                                                     }
01151 
01152                                                                                   return true;
01153                                                                                 }
01154 
01155 
01156     bool                                                                        CopyBufferRenderToBufferScreen    (XBYTE* buffer, XDWORD width, XDWORD height, bool isinverse )
01157                                                                                 {
01158                                                                                   GRPRECTINT* rectscreen  = GetScreenZone();
01159                                                                                   if(!rectscreen) return false;
01160 
01161                                                                                   agg::rendering_buffer screenbuffer;
01162 
01163                                                                                   screenbuffer.attach(buffer , width
01164                                                                                                              , height
01165                                                                                                              , (isinverse?1:-1) * ((int)width * GetBytesperPixel()));
01166 
01167                                                                                   int nlines    = (rectscreen->y2-rectscreen->y1);
01168                                                                                   int sizeline  = (rectscreen->x2-rectscreen->x1) * GetBytesperPixel();
01169                                                                                   int xpos      = (rectscreen->x1 * GetBytesperPixel());
01170 
01171                                                                                   int c         = 0;
01172 
01173                                                                                   while(c < nlines)
01174                                                                                     {
01175                                                                                       memcpy(screenbuffer.row_ptr(c), (rbuffer.row_ptr(rectscreen->y1+c) + xpos), sizeline);
01176                                                                                       c++;
01177                                                                                     }
01178 
01179                                                                                   return true;
01180                                                                                 }
01181 
01182 
01183     bool                                                                        CopyBufferRenderFromViewport      (GRPVIEWPORT* viewport)
01184                                                                                 {
01185                                                                                   if(!viewport) return false;
01186 
01187                                                                                   GRPCANVAS*  origincanvas = viewport->GetCanvas();
01188                                                                                   if(!origincanvas) return false;
01189 
01190                                                                                   GRPRECTINT  positionrect;
01191 
01192                                                                                   positionrect.x1 = GetScreenZone()->x1 + (int)viewport->GetPositionX();
01193                                                                                   positionrect.y1 = GetScreenZone()->y1 + (int)viewport->GetPositionY();
01194 
01195                                                                                   positionrect.x2 = positionrect.x1 + (int)viewport->GetWidth();
01196                                                                                   positionrect.y2 = positionrect.y2 + (int)viewport->GetHeight();
01197 
01198                                                                                   agg::rendering_buffer origincanvasbuffer;
01199 
01200                                                                                   origincanvasbuffer.attach(origincanvas->GetBuffer(), origincanvas->GetWidth()
01201                                                                                                                                      , origincanvas->GetHeight()
01202                                                                                                                                      , (origincanvas->IsBufferInverse()?1:-1) * ((int)origincanvas->GetWidth() * origincanvas->GetBytesperPixel()));
01203 
01204                                                                                   int nlines        = (int)viewport->GetHeight();
01205                                                                                   int sizeline      = (int)(viewport->GetWidth() * GetBytesperPixel());
01206                                                                                   int originxpos    = (origincanvas->GetScreenZone()->x1 * GetBytesperPixel());
01207                                                                                   int targetxpos    = (positionrect.x1 * GetBytesperPixel());
01208                                                                                   int c             = 0;
01209 
01210                                                                                   while(c < nlines)
01211                                                                                     {
01212                                                                                       memcpy( rbuffer.row_ptr(positionrect.y1 + c) + targetxpos                              ,
01213                                                                                               origincanvasbuffer.row_ptr(origincanvas->GetScreenZone()->y1 + c) + originxpos ,
01214                                                                                               sizeline);
01215                                                                                       c++;
01216                                                                                     }
01217 
01218                                                                                   return true;
01219                                                                                 }
01220 
01221 
01222     bool                                                                        ReleaseDrawFramerate              ()  
01223                                                                                 {                                                                                                                                                                                                                                                      
01224                                                                                   if(framerate_bitmap)
01225                                                                                     {
01226                                                                                       int height = (int)RasterFont_GetHeight();
01227                                                                                       PutBitmap(framerate_x-2 , (framerate_y-height+2), framerate_bitmap);
01228 
01229                                                                                       delete framerate_bitmap;
01230                                                                                       framerate_bitmap = NULL;
01231                                                                                     }
01232                                                                                                                                                                    
01233                                                                                   return true;
01234                                                                                 }
01235 
01236 
01237 
01238     bool                                                                        DrawFramerate                     (double x, double y, GRPSCREEN* screen)
01239                                                                                 {
01240                                                                                   if(!screen)  return false;
01241 
01242                                                                                   XSTRING           text;
01243                                                                                   GRP2DCOLOR_RGBA8  colorback(255, 255, 255);
01244                                                                                   GRP2DCOLOR_RGBA8  color(0, 0, 0);
01245 
01246                                                                                   text.Format(__L("%2.2f fps"), screen->GetFrameRate()->Get());
01247 
01248                                                                                   framerate_x = x;
01249                                                                                   framerate_y = y;
01250 
01251                                                                                   int     width       = (int)RasterFont_GetWidth(text.Get());
01252                                                                                   int     height      = (int)RasterFont_GetHeight();
01253                                                                                   //double  excessedge  = 10;
01254                                                                                 
01255                                                                                   
01256                                                                                   framerate_bitmap = GetBitmap(x-2, (y-height+2), width + 6 , height + 6);
01257                                                                                   
01258 
01259                                                                                   SetFillColor(&colorback);
01260                                                                                   SetLineColor(&color);                                                                                   
01261                                                                                   Rectangle(x-2, y+6, x + width +2 , y - height,  true);
01262                                                                                                                                                                     
01263                                                                                   RasterFont_SetColor(&color);
01264                                                                                   RasterFont_Printf(x, y, text.Get());
01265 
01266                                                                                   return true;
01267                                                                                 }
