00636 GRPVIEWPORT* GRPSCREEN::GetViewport(XCHAR* ID)
00637 {
00638   for(XDWORD c=0; c<viewports.GetSize(); c++)
00639     {
00640       GRPVIEWPORT* viewport = viewports.Get(c);
00641       if(viewport) 
